# 		Type: 					Integer
# 		Default: 				151
# 		Min: 						1
# 		Max: 						100000
#
# 		max permitted number of simultaneous client conns.
#
# max_delayed_threads
#
# 		cmd line format: 		--max-delayed-threads=#
# 		deprecated: 			Yes
# 		Sys var: 				max_delayed_threads
# 		Scope: 					Global, Session
# 		Dynamic: 				Yes
# 		SET_VAR Hint: 			No
# 		Type: 					Integer
# 		Default: 				20
# 		Min: 						0
